import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { Studentadmin } from './Studentadmin';

export const AdminDashboard = () => {
  const [mode, setMode] = useState('student'); // Initialize mode as 'student'

  return (
    <div className="flex flex-col items-center justify-center p-6 w-screen bg-gray-100 rounded-lg shadow-lg space-y-4">
      <div className="flex space-x-4">
        {/* Buttons to switch between modes */}
        <button
          onClick={() => setMode('student')}
          className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-600 transition-colors duration-300"
        >
          Student
        </button>
        <button
          onClick={() => setMode('visitor')}
          className="bg-gray-500 text-white font-bold py-2 px-4 rounded hover:bg-gray-600 transition-colors duration-300"
        >
          Visitor
        </button>
      </div>

      {/* Conditionally render StudentAdmin if mode is 'student' */}
      {mode === 'student' && (
        <div className="mt-4">
          <Studentadmin />
        </div>
      )}

      {/* You can add visitor-related content here if needed */}
      {mode === 'visitor' && (
        <div className="p-4 bg-green-100 border border-green-300 rounded-md mt-4">
          <h2 className="text-xl font-semibold text-green-700">Visitor Area</h2>
          <p className="mt-2 text-green-600">Welcome to the visitor section!</p>
        </div>
      )}
    </div>
  );
};
