import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export const AdminDashboard = () => {
  const [outpassRequests, setOutpassRequests] = useState([]);
  const [visitorRequests, setVisitorRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [viewMode, setViewMode] = useState('current');
  const [studentOutpasses, setStudentOutpasses] = useState([]);
  const [profileView, setProfileView] = useState(false);
  const [isVisitorMode, setIsVisitorMode] = useState(false);
  const apiUrl = process.env.REACT_APP_API_URL;
  const { adminlogout } = useAuth();

  useEffect(() => {
    const fetchOutpassRequests = async () => {
      try {
        const response = await fetch(`${apiUrl}/api/admin/outpass-requests`);
        if (!response.ok) throw new Error('Network response was not ok');
        const data = await response.json();
        setOutpassRequests(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching outpass requests:', error);
        setLoading(false);
      }
    };

    const fetchVisitorRequests = async () => {
      try {
        const response = await fetch(`${apiUrl}/api/admin/visitor-requests`);
        if (!response.ok) throw new Error('Network response was not ok');
        const data = await response.json();
        setVisitorRequests(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching visitor requests:', error);
        setLoading(false);
      }
    };

    if (isVisitorMode) {
      fetchVisitorRequests();
    } else {
      fetchOutpassRequests();
    }
  }, [apiUrl, isVisitorMode]);

  const fetchStudentOutpasses = async (Rollno) => {
    try {
      const response = await fetch(`${apiUrl}/api/student/student-outpasses/${Rollno}`);
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.json();
      setStudentOutpasses(data);
    } catch (error) {
      console.error('Error fetching student outpasses:', error);
    }
  };

  const handleUpdateStatus = async (_id, status) => {
    try {
      const response = await fetch(`${apiUrl}/api/admin/outpass-requested/${_id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status }),
      });
      if (!response.ok) throw new Error('Network response was not ok');
      setOutpassRequests(outpassRequests.map(request =>
        request._id === _id ? { ...request, status } : request
      ));
    } catch (error) {
      console.error('Error updating outpass request status:', error);
    }
  };

  const handleUpdateVisitorStatus = async (_id, status) => {
    try {
      const response = await fetch(`${apiUrl}/api/admin/visitor-requested/${_id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status }),
      });
      if (!response.ok) throw new Error('Network response was not ok');
      setVisitorRequests(visitorRequests.map(request =>
        request._id === _id ? { ...request, status } : request
      ));
    } catch (error) {
      console.error('Error updating visitor request status:', error);
    }
  };

  const filteredRequests = (isVisitorMode ? visitorRequests : outpassRequests).filter(request =>
    request.student ? request.student.Rollno.toLowerCase().includes(searchQuery.toLowerCase()) : request.visitorName.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const generatePDF = () => {
    const input = document.getElementById('student-history-table');

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF({
        orientation: 'portrait',
        unit: 'mm',
        format: 'a4',
      });

      const imgWidth = 190; // Width of the image in mm
      const pageHeight = 295; // Height of the page in mm
      const imgHeight = canvas.height * imgWidth / canvas.width;
      let heightLeft = imgHeight;
      let position = 0;
      const margin = 10; // Margin in mm

      // Add the first page with content
      pdf.addImage(imgData, 'PNG', margin, position + margin, imgWidth, imgHeight);
      heightLeft -= (pageHeight - 2 * margin);

      // Add additional pages if necessary
      while (heightLeft > 0) {
        position = heightLeft - imgHeight;
        pdf.addPage();
        pdf.addImage(imgData, 'PNG', margin, position + margin, imgWidth, imgHeight);
        heightLeft -= (pageHeight - 2 * margin);
      }

      pdf.save(`Student_History--[${new Date}].pdf`);
    });
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="p-8 bg-gray-100 min-h-screen w-[98%]">
      <h1 className="text-3xl font-bold text-center mb-8">Admin Dashboard</h1>

      <div className="flex justify-between mb-6">
        <button
          onClick={() => setIsVisitorMode(false)}
          className={`py-2 px-4 rounded ${!isVisitorMode ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`}
        >
          Student Mode
        </button>
        <button
          onClick={() => setIsVisitorMode(true)}
          className={`py-2 px-4 rounded ${isVisitorMode ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`}
        >
          Visitor Mode
        </button>
      </div>

      <div className="relative mb-3 lg:ml-[90%] md:ml-[80%] ml-[40%] h-fit">
        <button
          onClick={() => setProfileView(!profileView)}
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded shadow-md transition-all duration-300 ease-in-out"
        >
          Profile
        </button>
        {profileView && (
          <div className="w-fit top-12 right-0 bg-white border border-gray-300 rounded-lg shadow-lg p-4 mt-2 z-10">
            <button
              onClick={adminlogout}
              className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded shadow-md transition-all duration-300 ease-in-out w-full text-center"
            >
              Logout
            </button>
          </div>
        )}
      </div>

      <div className="mb-6 flex items-center gap-4 justify-around">
        <button
          onClick={() => setViewMode('current')}
          className={`py-2 px-4 rounded ${viewMode === 'current' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`}
        >
          {isVisitorMode ? 'Current Visitor Requests' : 'Current Outpass Requests'}
        </button>
        <button
          onClick={() => setViewMode('past')}
          className={`py-2 px-4 rounded ${viewMode === 'past' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`}
        >
          {isVisitorMode ? 'Visitor History' : 'Student History'}
        </button>
      </div>

      {viewMode === 'current' && (
        <div className="mb-6">
          <input
            type="text"
            placeholder={isVisitorMode ? "Search by Visitor Name" : "Search by Roll No"}
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="border rounded py-2 px-4 w-full"
          />
        </div>
      )}

      {viewMode === 'past' && (
        <div className="mb-6">
          <form
            onSubmit={(e) => {
              e.preventDefault();
              setStudentOutpasses([]);
              fetchStudentOutpasses(searchQuery);
            }}
          >
            <input
              type="text"
              placeholder="Search by Roll No"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="border rounded py-2 px-4 w-full"
            />
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded mt-2"
            >
              Search
            </button>
          </form>
        </div>
      )}

      <div id="student-history-table">
        <table className="min-w-full bg-white border border-gray-300">
          <thead>
            <tr>
              <th className="py-2 px-4 border-b">Student Name</th>
              <th className="py-2 px-4 border-b">Roll No</th>
              <th className="py-2 px-4 border-b">Outpass Type</th>
              <th className="py-2 px-4 border-b">Start Date</th>
              <th className="py-2 px-4 border-b">End Date</th>
              <th className="py-2 px-4 border-b">Status</th>
              <th className="py-2 px-4 border-b">Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredRequests.map((request) => (
              <tr key={request._id}>
                <td className="py-2 px-4 border-b">{request.student ? request.student.name : request.visitorName}</td>
                <td className="py-2 px-4 border-b">{request.student ? request.student.Rollno : request.visitorId}</td>
                <td className="py-2 px-4 border-b">{request.outpassType}</td>
                <td className="py-2 px-4 border-b">{request.startDate}</td>
                <td className="py-2 px-4 border-b">{request.endDate}</td>
                <td className="py-2 px-4 border-b">{request.status}</td>
                <td className="py-2 px-4 border-b">
                  <button
                    onClick={() => handleUpdateStatus(request._id, 'Approved')}
                    className="bg-green-500 hover:bg-green-600 text-white font-bold py-1 px-2 rounded"
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => handleUpdateStatus(request._id, 'Rejected')}
                    className="bg-red-500 hover:bg-red-600 text-white font-bold py-1 px-2 rounded ml-2"
                  >
                    Reject
                  </button>
                  <button
                    onClick={() => handleUpdateVisitorStatus(request._id, 'Approved')}
                    className="bg-green-500 hover:bg-green-600 text-white font-bold py-1 px-2 rounded"
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => handleUpdateVisitorStatus(request._id, 'Rejected')}
                    className="bg-red-500 hover:bg-red-600 text-white font-bold py-1 px-2 rounded ml-2"
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {viewMode === 'past' && studentOutpasses.length > 0 && (
        <button
          onClick={generatePDF}
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded mt-4"
        >
          Generate PDF
        </button>
      )}
    </div>
  );
};
