import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import Modal from "react-modal";
import jsPDF from "jspdf";
import "jspdf-autotable"; // Import the autoTable
Modal.setAppElement("#root");

function VisitorDashboard() {
  const { visitoruser, visitorlogout } = useAuth();
  const [outpassRequests, setOutpassRequests] = useState([]);
  const [fromTime, setFromTime] = useState("");
  const [toTime, setToTime] = useState("");
  const [reason, setReason] = useState("");
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [activeTab, setActiveTab] = useState("pending");
  const [selectedOutpass, setSelectedOutpass] = useState(null);
  const [profileView, setProfileView] = useState(false);
  const user=visitoruser;
  const apiUrl = process.env.REACT_APP_API_URL;

  useEffect(() => {
    const fetchOutpassHistory = async () => {
      try {
        const token = localStorage.getItem("token-visitor");
        const response = await fetch(`${apiUrl}/api/visitor/outpass-history`, {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error("Failed to fetch outpass history");
        }
        const data = await response.json();
        setOutpassRequests(data.outpasses);
      } catch (err) {
        console.error(err);
      }
    };

    fetchOutpassHistory();
  }, [apiUrl]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate the 'From Time' and 'To Time'
    if (new Date(fromTime) >= new Date(toTime)) {
      setError("From time must be earlier than To time.");
      return;
    }

    // Check for overlapping outpasses
    const hasOverlap = outpassRequests.some((outpass) => {
      const outpassFromTime = new Date(outpass.fromTime);
      const outpassToTime = new Date(outpass.toTime);
      const newFromTime = new Date(fromTime);
      const newToTime = new Date(toTime);

      return (
        (newFromTime >= outpassFromTime && newFromTime < outpassToTime) ||
        (newToTime > outpassFromTime && newToTime <= outpassToTime) ||
        (newFromTime <= outpassFromTime && newToTime >= outpassToTime)
      );
    });

    if (hasOverlap) {
      setError("You already have an outpass during this time period.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await fetch(`${apiUrl}/api/visitor/request-outpass`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ fromTime, toTime, reason }),
      });

      if (!response.ok) {
        throw new Error("Failed to request outpass");
      }

      const data = await response.json();

      // Update the outpassRequests state with the new outpass
      setOutpassRequests([...outpassRequests, data.outpass]);

      // Clear the form and show success message
      setSuccessMessage("Outpass request submitted successfully!");
      setFromTime("");
      setToTime("");
      setReason("");
      setError("");
    } catch (err) {
      console.error(err);
      setError("Failed to request outpass. Please try again.");
    }
  };

  const generatePDF = (outpass) => {
    const doc = new jsPDF();

    // Title
    doc.setFontSize(20);
    doc.setFont("helvetica", "bold");
    doc.setTextColor(0, 0, 255); // Blue color for title
    doc.text("Visitor Outpass Details", 20, 20);

    // Add a line separator
    doc.setLineWidth(0.75);
    doc.setDrawColor(0, 0, 255); // Blue color for separator
    doc.line(20, 22, 190, 22);

    // Big Box Border
    doc.setDrawColor(0, 0, 255); // Blue color for box border
    doc.setLineWidth(0.5);
    doc.rect(15, 25, 180, 160); // Adjusted height for better fit

    // Visitor Details Section
    doc.setFontSize(14);
    doc.setFont("helvetica", "bold");
    doc.setTextColor(0, 0, 255); // Blue color for section heading
    doc.text("Visitor Information", 20, 35);

    doc.setFontSize(12);
    doc.setFont("helvetica", "normal");
    doc.setTextColor(0, 0, 0); // Black color for text
    const visitorInfo = [
      `Name: ${user.visitorName}`,
      `Contact: ${user.visitorContact}`,
      `Email: ${user.visitoremail}`,
    ];
    visitorInfo.forEach((line, index) => {
      doc.text(line, 20, 45 + index * 10);
    });

    // Outpass Details Section
    doc.setFontSize(14);
    doc.setFont("helvetica", "bold");
    doc.setTextColor(0, 0, 255); // Blue color for section heading
    doc.text("Outpass Details", 20, 85);

    doc.setFontSize(12);
    doc.setFont("helvetica", "normal");
    doc.setTextColor(0, 0, 0); // Black color for text
    const outpassDetails = [
      `From: ${new Date(outpass.fromTime).toLocaleString()}`,
      `To: ${new Date(outpass.toTime).toLocaleString()}`,
      `Reason: ${outpass.reason}`,
      `Status: ${outpass.status}`,
    ];
    outpassDetails.forEach((line, index) => {
      doc.text(line, 20, 95 + index * 10);
    });

    // Footer
    doc.setFontSize(10);
    doc.setFont("helvetica", "italic");
    doc.setTextColor(100, 100, 100); // Gray color for footer
    doc.text(`Generated on ${new Date().toLocaleDateString()}`, 20, 150);

    // Save the PDF
    const formattedDate = new Date(outpass.fromTime)
      .toISOString()
      .replace(/[-T:\.Z]/g, "");
    doc.save(`${user.visitorName}-outpass-${formattedDate}.pdf`);
  };

  const approvedOutpasses = outpassRequests.filter(
    (outpass) => outpass.status === "approved"
  );
  const pendingOutpasses = outpassRequests.filter(
    (outpass) => outpass.status === "pending"
  );
  const pastOutpasses = outpassRequests.filter(
    (outpass) => new Date(outpass.toTime) < new Date()
  );

  const renderOutpassList = () => {
    let outpasses = [];
    let label = "";

    switch (activeTab) {
      case "approved":
        outpasses = approvedOutpasses;
        label = "Approved Outpasses";
        break;
      case "pending":
        outpasses = pendingOutpasses;
        label = "Pending Outpasses";
        break;
      case "past":
        outpasses = pastOutpasses;
        label = "Past Outpasses";
        break;
      default:
        break;
    }

    return (
      <div>
        <h3 className="text-2xl mb-6 font-semibold text-gray-800">{label}</h3>
        <div className="h-72 overflow-y-auto">
          {outpasses.length > 0 ? (
            <ul>
              {outpasses.map((outpass) => (
                <li
                  key={outpass._id}
                  className="mb-6 p-6 border-2 border-gray-300 rounded-lg shadow-xl bg-gray-100 hover:bg-gray-200 transition duration-200"
                >
                  <p className="text-xl">
                    <strong>From:</strong>{" "}
                    {new Date(outpass.fromTime).toLocaleString()}
                  </p>
                  <p className="text-xl">
                    <strong>To:</strong>{" "}
                    {new Date(outpass.toTime).toLocaleString()}
                  </p>
                  <p className="text-xl">
                    <strong>Reason:</strong> {outpass.reason}
                  </p>
                  <p className="text-xl font-semibold mb-4 flex items-center">
                    <span className="mr-2 font-bold text-gray-800">
                      Status:
                    </span>
                    <span
                      className={`px-3 py-1 rounded-full text-sm font-medium uppercase ${
                        outpass.status === "approved"
                          ? "bg-green-200 text-green-800"
                          : outpass.status === "pending"
                          ? "bg-yellow-200 text-yellow-800"
                          : "bg-red-200 text-red-800"
                      }`}
                    >
                      {outpass.status}
                    </span>
                  </p>
                  {outpass.status === "approved" && (
                    <button
                      onClick={() => generatePDF(outpass)}
                      className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                    >
                      Download PDF
                    </button>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p>No outpasses found</p>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Visitor Dashboard</h1>
      <button
        onClick={() => setProfileView(true)}
        className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 mb-4"
      >
        View Profile
      </button>
      <button
        onClick={() => visitorlogout()}
        className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 mb-4"
      >
        Logout
      </button>
      <form onSubmit={handleSubmit} className="mb-8">
        <h2 className="text-2xl mb-4 font-semibold text-gray-800">
          Request Outpass
        </h2>
        <div className="mb-4">
          <label htmlFor="fromTime" className="block text-lg font-medium text-gray-700">
            From Time
          </label>
          <input
            type="datetime-local"
            id="fromTime"
            value={fromTime}
            onChange={(e) => setFromTime(e.target.value)}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="toTime" className="block text-lg font-medium text-gray-700">
            To Time
          </label>
          <input
            type="datetime-local"
            id="toTime"
            value={toTime}
            onChange={(e) => setToTime(e.target.value)}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="reason" className="block text-lg font-medium text-gray-700">
            Reason
          </label>
          <textarea
            id="reason"
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            rows="4"
            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          />
        </div>
        {error && <p className="text-red-600">{error}</p>}
        {successMessage && <p className="text-green-600">{successMessage}</p>}
        <button
          type="submit"
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          Submit Request
        </button>
      </form>
      <div>
        <h2 className="text-2xl mb-6 font-semibold text-gray-800">Outpass History</h2>
        <div className="flex mb-4">
          <button
            onClick={() => setActiveTab("pending")}
            className={`px-4 py-2 rounded-lg ${
              activeTab === "pending" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800"
            }`}
          >
            Pending
          </button>
          <button
            onClick={() => setActiveTab("approved")}
            className={`ml-4 px-4 py-2 rounded-lg ${
              activeTab === "approved" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800"
            }`}
          >
            Approved
          </button>
          <button
            onClick={() => setActiveTab("past")}
            className={`ml-4 px-4 py-2 rounded-lg ${
              activeTab === "past" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800"
            }`}
          >
            Past
          </button>
        </div>
        {renderOutpassList()}
      </div>
      <Modal
        isOpen={profileView}
        onRequestClose={() => setProfileView(false)}
        className="bg-white p-6 rounded-lg shadow-lg max-w-md mx-auto"
      >
        <h2 className="text-2xl font-bold mb-4">Visitor Profile</h2>
        <p><strong>Name:</strong> {user.visitorName}</p>
        <p><strong>Contact:</strong> {user.visitorContact}</p>
        <p><strong>Email:</strong> {user.visitoremail}</p>
        <button
          onClick={() => setProfileView(false)}
          className="mt-4 px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700"
        >
          Close
        </button>
      </Modal>
    </div>
  );
}

export default VisitorDashboard;
